mapper.select:
    	select  c.*, t1.`cityName` AS countryName, t2.`cityName` AS provinceName, t3.`cityName` AS cityName  from 
		    	customer c 
		  LEFT JOIN city t1 
		    ON c.`countryId` = t1.`id` 
		  LEFT JOIN city t2 
		    ON c.`provinceId` = t2.`id` 
		  LEFT JOIN city t3 
		    ON c.`cityId` = t3.`id` 
    	
    		1=1
    		
		  		and c.customerName like "%${customerName}%" 
			
			
		  		and c.realName=#{realName}
			
			
		  		and c.email=#{email}
			
			
		  		and c.tel=#{tel}
			
			
		  		and c.birthday=#{birthday}
			
			
		  		and c.idCard=#{idCard}
			
			
		  		and c.passportNumber=#{passportNumber}
			
			
				and c.ctdt=#{ctdt}
			
			
				and c.ctuser=#{ctuser}
			
			
				and c.upuser=#{upuser}
			
			
				and c.updt=#{updt}
			
    	
		
			limit #{offset},#{limit}

    	select count(*) from customer 
    	
    		1=1
    		
		  		and customerName like "%${customerName}%" 
			
			
		  		and realName=#{realName}
			
			
		  		and email=#{email}
			
			
		  		and tel=#{tel}
			
			
		  		and birthday=#{birthday}
			
			
		  		and idCard=#{idCard}
			
			
		  		and passportNumber=#{passportNumber}
			
			
				and ctdt=#{ctdt}
			
			
				and ctuser=#{ctuser}
			
			
				and upuser=#{upuser}
			
			
				and updt=#{updt}
