mapper.select:
select package.*, p.*, c.cityName from package as package left join product as p on package.pid = p.pid left join city as c on p.country = c.id
package.del_flg=0
and p.pid = #{pid,jdbcType=VARCHAR}
and p.pname like "%"#{pname,jdbcType=VARCHAR}"%"
order by package.ctdt desc
limit #{offset},#{limit};

    select * from package as package
	left join product as p on package.pid = p.pid
    
    	
		  and p.pid = #{pid,jdbcType=VARCHAR}
