mapper.select:
select 

from room_type
where pid = #{pid,jdbcType=VARCHAR}
  and room_type = #{roomType,jdbcType=VARCHAR}

SELECT p.pname as pname, p.pname_en as panmeEn , rt.room_type as roomType, rt.pid as pid,rt.room_type_name as roomTypeName FROM room_type as rt left join hotel as h on h.pid = rt.pid left join product as p on p.pid = rt.pid
rt.del_flg=0
and rt.room_type = #{roomType,jdbcType=VARCHAR}
and rt.pid = #{pid,jdbcType=VARCHAR}

mapper.delete:
delete from room_type
where pid = #{pid,jdbcType=VARCHAR}
  and room_type = #{roomType,jdbcType=VARCHAR}
