mapper.select:
    	select * from evaluation
    	
    		1=1
			
		  		and customerName=#{customerName}
			
			
		  		and productId=#{productId}
			
			
				and ctdt=#{ctdt}
			
			
				and ctuser=#{ctuser}
			
			
				and upuser=#{upuser}
			
			
				and updt=#{updt}
			
    	
		
			limit #{offset},#{limit}

    	SELECT e.id,e.`customerName`, e.`productId`,e.`accountnumber`,e.`core`,e.`content`,e.`ctuser`,e.`ctdt`,e.`updt`,e.`upuser`,p.pname,p.pid 
FROM evaluation e LEFT JOIN product p ON p.`pid` = e.`productId`
		  
		  	1=1
		  	
		  	 AND e.`customerName` LIKE "%${customerName}%" 
		  	
		  	
		  		
		  		AND e.core>=#{minCore}
		  		   
		  	
		  	
		  	
		  		AND e.core<=#{maxCore}
		  		   
		  	
		  	
		  		AND p.ptype =#{productType}
		  	
		  
		
			limit #{offset},#{limit}

    	SELECT count(*) FROM evaluation e LEFT JOIN product p ON p.`pid` = e.`productId`
		  
		  	1=1
		  	
		  	 AND e.`customerName` LIKE "%${customerName}%" 
		  	
		  	
		  		
		  		AND e.core>=#{minCore}
		  		   
		  	
		  	
		  	
		  		AND e.core<=#{maxCore}
		  		   
		  	
		  	
		  		AND p.ptype =#{productType}
