mapper.select:
SELECT t.id,t.ctuser,t.themeCatalogId,t.isHomeShow,t.isThemeHomeShow,p.pname,p.ptype,p.supplier_no,p.country,p.pid, p.pstatus,p.startdate,p.enddate, c.cityName, s.s_name FROM product AS p LEFT JOIN city AS c ON p.country = c.id LEFT JOIN suppliers AS s ON p.supplier_no = s.sid LEFT JOIN themeproduct AS t ON p.`pid`=t.`productId` WHERE t.`themeCatalogId`=#{id}
and p.pname like "%${pname}%"
order by t.isHomeShow,t.updt desc
limit #{offset},#{limit};

    	select * from themecatalog
    	
    		1=1
    		
		  		and name like "%${name}%" 
			
			
		  		and themeId=#{themeId}
			
			
		  		and hasProduct=#{hasProduct}
			
			
			
				and ctdt=#{ctdt}
			
			
				and ctuser=#{ctuser}
			
			
				and upuser=#{upuser}
			
			
				and updt=#{updt}
			
    	
		
			limit #{offset},#{limit}

    	select * from themecatalog
    	
    		1=1
    		
