				and ctuser=#{ctuser}
			
			
				and upuser=#{upuser}
			
			
				and updt=#{updt}
			
    	
		
			limit #{offset},#{limit}

    	select count(*) from themecatalog 
    	
    		1=1
    		
		  		and name like "%${name}%" 
			
			
