mapper.select:
    	select * from theme
    	
    		1=1
    		
		  		and name like "%${name}%" 
			
			
		  		and isHome=#{isHome}
			
			
		  		and homeShowOrder=#{homeShowOrder}
			
			
		  		and isMore=#{isMore}
			
			
		  		and moreShowOrder=#{moreShowOrder}
			
			
				and ctdt=#{ctdt}
			
			
				and ctuser=#{ctuser}
			
			
				and upuser=#{upuser}
			
			
				and updt=#{updt}
			
    	
		
			limit #{offset},#{limit}

    	select * from theme
    	
    		1=1
    		
		  		and name like "%${name}%" 
			
			
		  		and isHome=#{isHome}
			
			
		  		and homeShowOrder=#{homeShowOrder}
			
			
		  		and isMore=#{isMore}
			
			
		  		and moreShowOrder=#{moreShowOrder}
			
			
				and ctdt=#{ctdt}
			
			
				and ctuser=#{ctuser}
			
			
				and upuser=#{upuser}
			
			
				and updt=#{updt}
			
    	
		
			limit #{offset},#{limit}

    	select count(*) from theme 
    	
    		1=1
    		
		  		and name like "%${name}%" 
			
			
		  		and isHome=#{isHome}
			
			
		  		and homeShowOrder=#{homeShowOrder}
			
			
		  		and isMore=#{isMore}
			
			
		  		and moreShowOrder=#{moreShowOrder}
			
			
				and ctdt=#{ctdt}
			
			
				and ctuser=#{ctuser}
			
			
				and upuser=#{upuser}
			
			
				and updt=#{updt}
