mapper.select:
SELECT s.sid,s.s_name, s.s_status,sc.cityName,s.s_remark,s.currency_type,sp.payName,s.pay_account,s.del_flg,s.ctdt,s.ctuser,s.updt,s.upuser ,m.c_name,m.c_email FROM suppliers s LEFT JOIN city sc ON s.s_country=sc.id LEFT JOIN s_pay sp ON s.pay_type=sp.pid LEFT JOIN (SELECT sid,c_name,c_email,max(updt) from suppliers_contractor where is_primary = 1 group by sid) m ON m.sid = s.sid where s.del_flg=0
and s.s_name like "%${s_name}%"
limit #{offset},#{limit};
