mapper.select:
select 

from traffic_chartered_price
where pid = #{pid,jdbcType=VARCHAR}
  and valid_starttime = #{validStarttime,jdbcType=VARCHAR}
  and carbrand = #{carbrand,jdbcType=VARCHAR}

mapper.delete:
delete from traffic_chartered_price
where pid = #{pid,jdbcType=VARCHAR}
  and valid_starttime = #{validStarttime,jdbcType=VARCHAR}
  and carbrand = #{carbrand,jdbcType=VARCHAR}
