mapper.select:
select

from package_price
where pid = #{pid,jdbcType=VARCHAR}
and starttime =
#{starttime,jdbcType=TIMESTAMP}
and price_item =
#{priceItem,jdbcType=VARCHAR}

select

from package_price

	del_flg=0

		and price_item like "%"#{priceItem,jdbcType=VARCHAR}"%"


		and price_group = #{priceGroup,jdbcType=VARCHAR}


		and pid = #{pid,jdbcType=VARCHAR}



  and enddate >=#{date,jdbcType=TIMESTAMP}
   and starttime <=#{date,jdbcType=TIMESTAMP}



order by order_num

	limit #{offset},#{limit};

mapper.delete:
delete from
package_price
where pid = #{pid,jdbcType=VARCHAR}
and starttime =
#{starttime,jdbcType=TIMESTAMP}
and price_item =
#{priceItem,jdbcType=VARCHAR}
