mapper.select:
    	select * from themecatalog

    		1=1

		  		and name like "%${name}%" 


		  		and themeId=#{themeId}


		  		and hasProduct=#{hasProduct}



				and ctdt=#{ctdt}


				and ctuser=#{ctuser}


				and upuser=#{upuser}


				and updt=#{updt}



			limit #{offset},#{limit}

    	select * from themecatalog

    		1=1

		  		and name like "%${name}%" 


		  		and themeId=#{themeId}


		  		and hasProduct=#{hasProduct}



				and ctdt=#{ctdt}


				and ctuser=#{ctuser}


				and upuser=#{upuser}


				and updt=#{updt}



			limit #{offset},#{limit}

    	select count(*) from themecatalog 

    		1=1

		  		and name like "%${name}%" 


		  		and themeId=#{themeId}


		  		and hasProduct=#{hasProduct}



				and ctdt=#{ctdt}


				and ctuser=#{ctuser}


				and upuser=#{upuser}


				and updt=#{updt}
