mapper.select:
    	select * from themeproduct

    		1=1

		  		and themeId=#{themeId}


		  		and productId=#{productId}


		  		and themeCatalogId=#{themeCatalogId}


		  		and isHomeShow=#{isHomeShow}


		  		and isThemeHomeShow=#{isThemeHomeShow},


				and ctdt=#{ctdt}


				and ctuser=#{ctuser}


				and upuser=#{upuser}


				and updt=#{updt}



			limit #{offset},#{limit}

    	select * from themeproduct

    		1=1

		  		and themeId=#{themeId}


		  		and themeCatalogId=#{themeCatalogId}


		  		and productId=#{productId}


		  		and isHomeShow=#{isHomeShow}


		  		and isThemeHomeShow=#{isThemeHomeShow},


				and ctdt=#{ctdt}


				and ctuser=#{ctuser}


				and upuser=#{upuser}


				and updt=#{updt}



			limit #{offset},#{limit}

    	select count(*) from themeproduct 

    		1=1

		  		and themeId=#{themeId}


		  		and themeCatalogId=#{themeCatalogId}


		  		and productId=#{productId}


		  		and isHomeShow=#{isHomeShow}


		  		and isThemeHomeShow=#{isThemeHomeShow},


				and ctdt=#{ctdt}


				and ctuser=#{ctuser}


				and upuser=#{upuser}


				and updt=#{updt}

    	select * from themeproduct as themeproduct
		left join product as product on themeproduct.productId = product.pid

    		themeproduct.themeId = #{themeId}

select themeproduct.*,product.*,country.cityName as countryName,c.cityName as cityName from themeproduct as themeproduct left join product as product on themeproduct.productId = product.pid LEFT JOIN city as country on (product.country = country.id) LEFT JOIN city as c on (product.city = c.id)
isHomeShow =0 and themeproduct.themeId = #{themeId}
order by themeproduct.updt asc
